import { Component } from 'react'

 import './index.css'
 class Speedometer extends Component{
    state ={fast:0};
    accelerate=()=>{
        this.setState(prevState=>({fast:prevState.fast+15}))
    }

    brake=()=>{
        this.setState(prevState=>({fast:prevState.fast-10}))
    }
    render()
    {
        const {fast}=this.state



        return(
            <div>
                <h1>SPEEDOMETER</h1>

                <img src="https://assets.ccbp.in/frontend/react-js/speedometer-img.png" alt=".avatar"/>

                <h1>Speed id {fast}</h1>
                <p>Min Limit is 0mph,Max Limit is 200mph</p>
                <div>
                    <button className="button1" id="b1" onClick={this.accelerate}>Accelerate</button>
                    <button className="button1" onClick={this.brake}>Apply Brake</button>
                </div>
            </div>
        )
    }
 }

 export default Speedometer;
